CommonElement.prototype.handlers.push({pattern:'input#roles',canApply:function(){return this.$element.is('#roles')},handler:function(){var $el=this.$element,rootValue=this.$element.data('root-value'),comment=$el.closest('.roles-value').find('.roles-comment');this.$element.change(function(){var value=$el.val();if(value&&value.indexOf(String(rootValue))!==-1){comment.show()}else comment.hide()})}})